import React from 'react';

class Login extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      buttonDisabled: true,
      email: '',
      password: '',
    };

    this.handleChange = this.handleChange.bind(this);
    this.enableBtn = this.enableBtn.bind(this);
  }

  componentDidUpdate() {
    const { email, password } = this.state;
    const checkEmail = /\S+@\S+\.\S+/;
    const MIN_PASS_LENGTH = 6;
    if (checkEmail.test(email) && password.length >= MIN_PASS_LENGTH) {
      // this.setState(() => ({ buttonDisabled: false }));
      // this.enableBtn();
    } 
    // else this.enableBtn();
  }

  enableBtn() {
    const { buttonDisabled } = this.state;
    if (buttonDisabled) this.setState({ buttonDisabled: false });
    else this.setState({ buttonDisabled: true });
  }

  handleChange({ target: { name, value } }) {
    // **Source: https://stackoverflow.com/questions/46155/how-to-validate-an-email-address-in-javascript **//
    this.setState({ [name]: value });
  }

  render() {
    const { buttonDisabled } = this.state;
    return (
      <div>
        <form onSubmit={ this.handleSubmit }>
          <div>
            <input
              data-testid="email-input"
              type="email"
              name="email"
              onChange={ this.handleChange }
              placeholder="email"
            />
            <input
              data-testid="password-input"
              type="password"
              name="password"
              onChange={ this.handleChange }
              placeholder="senha"
            />
          </div>
          <button type="submit" disabled={ buttonDisabled }> Entrar </button>
        </form>
      </div>
    );
  }
}

export default Login;
