import React from 'react';
import PropTypes from 'prop-types';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTrash } from '@fortawesome/free-solid-svg-icons';
import { setTotal, setWallet } from '../actions';
import { connect } from 'react-redux';

class WalletTable extends React.Component {
  constructor(props) {
    super(props);
    this.makeTable = this.makeTable.bind(this);
    this.deleteItem = this.deleteItem.bind(this);
  }

  deleteItem(item, expenses) {
    const { sendTotal, sendExpenses } = this.props;
    // console.log(item);
    // console.log(expenses);
    const updatedExpenses = expenses.filter(res => res.id !== item.id);
    let updatedTotal = 0;
    // console.log(updatedExpenses);
    if (updatedExpenses.length) {
      updatedTotal = updatedExpenses.reduce((acc, value) => {
        acc += value.convertedValue;
        return acc;
      }, 0);
      // console.log(updatedExpenses);
    }
    console.log(expenses);
    console.log(updatedExpenses);
    sendExpenses(updatedExpenses);
    sendTotal(updatedTotal);
  }



  makeTable(expenses) {
    return expenses.map((item) => (
      <tr key={ item.id }>
        <td>
          {item.description}
        </td>
        <td>
          {item.tag}
        </td>
        <td>
          {item.method}
        </td>
        <td>
          {item.value}
        </td>
        <td>
          {item.exchangeRates[item.currency].name}
        </td>
        <td>
          {parseFloat((item.exchangeRates[item.currency].ask)).toFixed(2)}
        </td>
        <td>
          {parseFloat(
            (item.exchangeRates[item.currency].ask * item.value
            ),
          ).toFixed(2)}
        </td>
        <td>
          Real
        </td>
        <td>
          <FontAwesomeIcon icon={ faTrash } onClick={ () => this.deleteItem(item, expenses) } />
        </td>
      </tr>
    ));
  }

  render() {
    const { wallet: { expenses } } = this.props;
    return (
      <table>
        <thead>
          <tr>
            <th> Descrição </th>
            <th> Tag </th>
            <th> Método de pagamento </th>
            <th> Valor </th>
            <th> Moeda </th>
            <th> Câmbio utilizado </th>
            <th> Valor convertido </th>
            <th> Moeda de conversão </th>
            <th> Editar/Excluir </th>
          </tr>
        </thead>
        <tbody>
          {expenses
            && this.makeTable(expenses)}
        </tbody>
      </table>
    );
  }
}

const mapStateToProps = (state) => ({
  wallet: state.wallet,
});

const mapDispatchToProps = (dispatch) => ({
  sendTotal: (total) => dispatch(setTotal(total)),
  sendExpenses: (expenses) => dispatch(setWallet(expenses)),
});

export default connect(mapStateToProps, mapDispatchToProps)(WalletTable);

WalletTable.propTypes = {
  wallet: PropTypes.shape({
    expenses: PropTypes.arrayOf(PropTypes.object),
  }).isRequired,
};
