import React from "react";

export default class WalletTable extends React.Component {
  constructor(props) {
    super(props);

    function makeTable(expenses) {
      return expenses.map((item) => (
        <tr key={item.id}>
          {console.log(item)}
          <td>
            {item.description}
          </td>
          <td>
            {item.tag}
          </td>
          <td>
            {item.method}
          </td>
          <td> {item.value}  </td>
          <td>
            {item.exchangeRates[item.currency].name}
          </td>
          <td>
            {parseFloat((item.exchangeRates[item.currency].ask)).toFixed(2)}
          </td>
          <td>
            {parseFloat(
              (item.exchangeRates[item.currency].ask * item.value
              ),
            ).toFixed(2)}
          </td>
        </tr>
      ));
    }
  }

  render() {
    const { wallet: { expenses } } = this.props;
    return (
      <table>
        <tr>
          <th> Descrição </th>
          <th> Tag </th>
          <th> Método de Pagamento </th>
          <th> Valor </th>
          <th> Moeda </th>
          <th> Câmbio utilizado </th>
          <th> Valor Convertido </th>
          <th> Moeda de conversão </th>
          <th> Editar/Excluir </th>
        </tr>
        {expenses
          && makeTable(expenses)}
      </table>
    );
  }
}
