import React from 'react';
import PropTypes from 'prop-types';

export default class WalletForm extends React.Component {
  render() {
    const { wallet: { currencies }, onChange, onClick } = this.props;
    const { currency } = this.state;
    currencies && this.setState({ currency: currencies })
    return (
      <form>
        <label htmlFor="valor">
          Valor:
          <input onChange={onChange} name="value" type="number" id="valor" />
        </label>
        {' '}
        <label htmlFor="moeda">
          Moeda:
          <select name="currency" id="moeda">
            {
              currency
              &&
              currency.map((coin, index) => <option key={ index }>{coin}</option>)
            }
          </select>
        </label>
        {' '}
        <label htmlFor="pagamento">
          Método de pagamento:
          <select id="pagamento">
            <option name="method" value="Dinheiro"> Dinheiro </option>
            <option name="method" value="crédito"> Cartão de crédito </option>
            <option name="method" value="débito"> Cartão de débito </option>
          </select>
        </label>
        {' '}
        <label htmlFor="tag">
          Tag:
          <select name="tag" id="tag">
            <option value="Alimentação"> Alimentação </option>
            <option value="Lazer"> Lazer </option>
            <option value="Trabalho"> Trabalho </option>
            <option value="Transporte"> Transporte </option>
            <option value="Saúde"> Saúde </option>
          </select>
        </label>
        {' '}
        <label htmlFor="desc">
          Descrição:
          <textarea name="description" id="desc" style={ { verticalAlign: 'top' } } />
        </label>
        <button type="button" onClick={onClick}> Adicionar Despesa </button>
      </form>);
  }
}

WalletForm.propTypes = {
  wallet: PropTypes.shape({
    currencies: PropTypes.arrayOf(PropTypes.string),
  }).isRequired,
};
