import { USER_INFO } from "../actions";

const INITIAL_STATE = {
  user: {},
};

const user = (state = INITIAL_STATE, action) => {
  switch (action.type) {
  case USER_INFO:
    return { ...state, user: action.payload };
  default:
    return state;
  }
};

export default user;
