import React from 'react';
import PropTypes from 'prop-types';

export default class WalletForm extends React.Component {
  render() {
    const { wallet: { currencies }, onChange, onClick, editItem } = this.props;
    const { expense: { value, currency, method, tag, description } } = this.state;
    return (
      <form>
        <label htmlFor="valor">
          Valor:
          <input onChange={ onChange } name="value" type="number" id="valor" />
        </label>
        {' '}
        <label htmlFor="moeda">
          Moeda:
          <select name="currency" id="moeda" onChange={ onChange }>
            {currencies
              && currencies.map((coin, index) => <option key={ index }>{coin}</option>)}
          </select>
        </label>
        {' '}
        <label htmlFor="pagamento">
          Método de pagamento:
          <select name="method" id="pagamento" onChange={ onChange }>
            <option> Dinheiro </option>
            <option> Cartão de crédito </option>
            <option> Cartão de débito </option>
          </select>
        </label>
        {' '}
        <label htmlFor="tag">
          Tag:
          <select name="tag" id="tag" onChange={ onChange }>
            <option value="Alimentação"> Alimentação </option>
            <option value="Lazer"> Lazer </option>
            <option value="Trabalho"> Trabalho </option>
            <option value="Transporte"> Transporte </option>
            <option value="Saúde"> Saúde </option>
          </select>
        </label>
        {' '}
        <label htmlFor="desc">
          Descrição:
          <textarea
            name="description"
            id="desc"
            style={ { verticalAlign: 'top' } }
            onChange={ onChange }
          />
        </label>
        {editItem ? <button type="button" onClick={ onClick }> Editar </button> : <button type="button" onClick={ onClick }> Adicionar Despesa </button> }
      </form>);
  }
}

WalletForm.propTypes = {
  onChange: PropTypes.func.isRequired,
  onClick: PropTypes.func.isRequired,
  wallet: PropTypes.shape({
    currencies: PropTypes.arrayOf(PropTypes.string),
  }).isRequired,
};
