import { COIN_INFO, EXPENSES } from '../actions';

// const INITIAL_STATE = {
//   currencies: [],
//   expenses: [],
// };

const wallet = (state = {}, action) => {
  switch (action.type) {
  case COIN_INFO:
    return { ...state, currencies: action.payload };
  case EXPENSES:
    return { ...state, expenses: action.payload };
  default:
    return state;
  }
};

export default wallet;
