import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';

class WalletHeader extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      total: 0,
    };
  }

  setTotal() {
    const { wallet } = this.props;
    const total = wallet.expenses.reduce((acc, { value, currency, exchangeRates }) => (
      acc + parseFloat(value * exchangeRates[currency].ask)
    ), 0).toFixed(2);
    this.setState({ total });
  }

  render() {
    const { user: { email }, total, moeda } = this.props;
    return (
      <div>
        <span data-testid="email-field">
          Email:
          {' '}
          {email}
        </span>
        {' '}
        |
        {' '}
        <span data-testid="total-field">
          Despesa Total: R$
          {total}
        </span>
        {' '}
        <span data-testid="header-currency-field">
          {moeda}
        </span>
      </div>);
  }
}

const mapStateToProps = (state) => ({
  user: state.user,
  total: state.wallet.expenses.reduce((acc, { value, currency, exchangeRates }) => (
    acc + parseFloat(value * exchangeRates[currency].ask)
  ), 0).toFixed(2),
});

export default connect(mapStateToProps)(WalletHeader);

WalletHeader.propTypes = {
  user: PropTypes.shape({
    email: PropTypes.string,
  }).isRequired,
  total: PropTypes.string,
  moeda: PropTypes.string.isRequired,
};

WalletHeader.defaultProps = {
  total: 0,
};
