import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import WalletHeader from './WalletHeader';
import { fetchCoins, setExpenses } from '../actions';
import WalletForm from './WalletForm';
import store from '../store';

class Wallet extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      expense: {
        value: 0,
        currency: 'USD',
        method: 'Dinheiro',
        tag: 'Alimentação',
      },
      total: 0,
      moeda: 'BRL',
      arrayLen: 0,
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleButton = this.handleButton.bind(this);
    this.setTotal = this.setTotal.bind(this);
  }

  componentDidMount() {
    const { sendCoin } = this.props;
    sendCoin();
  }

  componentDidUpdate() {
    this.setTotal(store.getState().wallet.expenses);
    // console.log(store.getState().wallet);
  }

  setTotal(arr) {
    const { arrayLen, expense, total } = this.state;
    if (arrayLen < arr.length) {
      const current = arr[arr.length - 1];
      const conversionValue = current.exchangeRates[expense.currency];
      const conversion = (expense.value * conversionValue.ask).toFixed(2);
      const soma = (total + Number(conversion)).toFixed(2);
      const counter = arrayLen + 1;
      console.log(conversion);
      this.setState({
        total: soma,
        arrayLen: counter,
      });
    }
    // const item = expenses[expenses.length - 1];
    // if (item) {
    //   const test = item.exchangeRates[item.currency];
    //   console.log(test);
    //   console.log(item);
    //   const conversion = (item.value * test.ask).toFixed(2);
    //   return conversion;
    // }
  }

  handleChange({ target: { name, value } }) {
    const { wallet: { expenses } } = this.props;
    this.setState((prevState) => ({
      expense: {
        ...prevState.expense,
        [name]: value,
        id: expenses.length,
      },
    }));
  }

  handleButton() {
    const { sendExpenses, wallet } = this.props;
    const { expense, total } = this.state;
    sendExpenses(expense);
    // this.setTotal(expense);
  }

  render() {
    const { user, wallet } = this.props;
    const { total, moeda } = this.state;
    return (
      <div>
        <WalletHeader total={ total } moeda={ moeda } user={ user } />
        <br />
        <WalletForm wallet={ wallet } onChange={ this.handleChange } onClick={ this.handleButton } />
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  user: state.user,
  wallet: state.wallet,
});

const mapDispatchToProps = (dispatch) => ({
  sendCoin: () => dispatch(fetchCoins()),
  sendExpenses: (expense) => dispatch(setExpenses(expense)),
});

export default connect(mapStateToProps, mapDispatchToProps)(Wallet);

Wallet.propTypes = {
  sendCoin: PropTypes.func.isRequired,
  user: PropTypes.shape({
    email: PropTypes.string,
  }).isRequired,
  wallet: PropTypes.shape({
    currencies: PropTypes.arrayOf(PropTypes.string),
  }).isRequired,
};
